patterns.template:
class StageN_Capability extends StageNMinus1 {
  enhance(prompt, context) {
    // Add ONE capability
    const enhanced = this.applyCapability(prompt, context);
    return super.enhance(enhanced, context);
  }
}

type Message = { type: 'action', data: any } | { type: 'response', result: any };

async handle(message: Message) {
  try {
    switch(message.type) {
      case 'action': return await this.process(message.data);
    }
  } catch (error) {
    this.send({ type: 'error', message: String(error) });
  }
}

interface KnowledgeItem {
  state: 'checked' | 'unchecked' | 'locked';
  toggle(): void {
    if (this.state === 'locked') return;
    this.state = this.state === 'checked' ? 'unchecked' : 'checked';
  }
}

async processError(error) {
  const pattern = extractPattern(error);
  const similar = findSimilar(pattern);
  const action = await showRecoveryUI(error, similar);
  if (action === 'save') {
    const learning = createLearning(pattern);
    await save(learning);
    applyImmediately(learning);
  }
}

render() {
  let html = this.renderEssential();
  if (level !== 'essential') html += this.renderCommon();
  if (level === 'advanced') html += this.renderAdvanced();
  return html;
}

