class Stage10_EmailValidation extends Stage9_PackageAware {
  enhance(prompt, context) {
    if (prompt.includes('email')) {
      prompt += '\nEnsure email validation follows RFC 5322';
    }
    return super.enhance(prompt, context);
  }
}

